    // Called when a field is changed in a managed form.
    onFieldChanged: function(event) {
    	//Plus 18 policies
        if (event.screen === 'gigya-subscribe-with-email-screen') {
          if (event.field === 'data.birthDate') {
              var birthDateCalendar;
              birthDateCalendar = event.value;
 
              // Convert birthDateCalendar to a Date object
              var birthDateObject = new Date(birthDateCalendar);
 
              // Get the current date
              var currentDate = new Date();
 
              // Calculate the date 18 years ago from the current date
              var eighteenYearsAgo = new Date(currentDate.getFullYear() - 18, currentDate.getMonth(), currentDate.getDate());
 
              //Get all subscriptions fields that have the CSS class "subscriptionToggle18"
              var subscriptionToggle18Elements = document.querySelectorAll('.subscriptionToggle18');
 
              subscriptionToggle18Elements.forEach(function (subscriptionToggle18) {
                  var checkboxElement = subscriptionToggle18.querySelector("input[type='checkbox']");
 
                  // Check if user is <18 and set display:none in subscription if that's the case
                  if (birthDateObject > eighteenYearsAgo) {
                      subscriptionToggle18.style.setProperty('display', 'none', 'important');
 
                      // Uncheck checkbox
                      if (checkboxElement) {
                          checkboxElement.checked = false;
                      }
                  } else {
                      subscriptionToggle18.style.removeProperty('display');
                  }
              });
          }
      }
    },

    // Called when a user clicks the "X" (close) button or the screen is hidden following the end of the flow.
    onHide: function(event) {
    }
